/**
 * Set NavTrigger Action
 */

'use strict';
var debug = require('debug');

/**
 * Update Navtrigger state
 * @method setNavTrigger
 * @param {Object} context Flux action context. https://github.com/yahoo/fluxible-app
 * @param {Object} [payload] Action payload
 * @param {Function} done
 */
module.exports = function setNavTrigger(context, payload, done) {
    debug('setNavTrigger', payload);
    payload = payload || {};
    context.dispatch('SET_NAVTRIGGER', payload);
    done && done();
};
