'use strict';

var fumble = require('fumble');

module.exports = function loadTheme(context, payload, done) {
    var data = {};
    data.payload = payload;

    if (payload && payload.getIn(['err', 'loadTheme'])) {
        done && done(fumble.http.internalServerError(payload.getIn(['err', 'loadTheme'])), null);
        return;
    }
    done && done(null, data);
};
