/*jslint indent:4, nomen:true, white:true, node:true */

'use strict';

var React = require('react/addons');

var Icon = React.createClass({
    getProps: function() {
        var props = this.props;
        return Object.keys(props).reduce(
            function (clonedProps, key) {
                clonedProps['data-' + key.toLowerCase()] = props[key];
                return clonedProps;
            }, {});
    },
    render: function() {
        return <div className='icon' { ... this.getProps() } />;
    }
});

module.exports = {
    Icon: Icon
};
