/**
 * @class CountStore
 */

'use strict';

var createStore = require('fluxible-immutable-utils').createImmutableStore;
var CountStore = createStore({
    data: {
        'THEME_FAILURE': 0,
        'THEME_SUCCESS': 0
    },

    handlers: {
        'THEME_FAILURE': '_handleThemeFailure',
        'THEME_SUCCESS': '_handleThemeSuccess'
    },

    storeName: 'CountStore',

    _handleThemeFailure: function() {
        this.data.THEME_FAILURE++;
    },

    _handleThemeSuccess: function() {
        this.data.THEME_SUCCESS++;
    },

    getData: function() {
        return this.data;
    },

    resetData: function() {
        this.data = {
            'THEME_FAILURE': 0,
            'THEME_SUCCESS': 0
        };
    }
});

module.exports = CountStore;
