/* global browser, $, it, describe, before */

var chai = require('chai');
var chaiAsPromised = require('chai-as-promised');
var conditions = require('../lib/conditions');
var expect = chai.expect;
var locators = require('../lib/locators');
var when = require('mocha-bdd-when');

chai.use(chaiAsPromised);

var loginUser = function() {
    browser.signIn('ucstester@yahoo.com', 'testing');
    browser.get('/console?ctrl=TabletHeader&m_id=tdv2-applet-uh&device=tablet');
};

describe('#check Profile Tablet', function () {
    before(function () {
        return browser.wait(function () {
            return browser.get('/console?ctrl=TabletHeader&m_id=tdv2-applet-uh&device=tablet').then(function () {
                return true;
            });
        });
    });

    when(conditions['#login']).describe('#login', function () {
        before(function () {
            loginUser();
        });

        it('should exist', function () {
            var profile = $(locators.ProfileMobile);
            browser.waitToBePresent(profile);
            expect(profile.isPresent()).not.to.equal(false);
        });


        it('profile link goes to edit.yahoo.com', function () {
            var profile = $(locators.ProfileMobile);
            browser.waitToBePresent(profile);
            profile.getAttribute('href').then(function(url) {
                expect(url).to.contain('yahoo.com');
            });
        });
    });
});
