/*global require, describe:true, it:true */

'use strict';

var _require = require('root-require');
var expect = require('chai').expect;
var Immutable = require('immutable');
var Path = require('path');
var recursiveReadSync = require('recursive-readdir-sync');

describe('lang/strings', function () {
    var base = _require('lang/strings.json');
    base = Immutable.fromJS(base);

    var matches;
    var path = Path.resolve(__dirname, '../../../lang/');
    var testFiles = recursiveReadSync(path);

    testFiles.forEach(function (file) {
        matches = file.match(/strings_(.*)\.json$/);

        if (matches && matches.length === 2) {
            var lang = require(file);
            lang = Immutable.fromJS(lang);

            it ('{ lang: ' + matches[1] + ' }', function () {
                expect(lang.keySeq().toArray()).to.be.deep.equals(base.keySeq().toArray());
            });
        }
    });
});
