/*global require, describe:true, it:true, beforeEach:true */

'use strict';

var _require = require('root-require');
var expect = require('chai').expect;
var fumble = require('fumble');
var Immutable = require('immutable');
var service = _require('services/theme');

describe('services/theme', function () {
    var params;
    var req;

    beforeEach(function () {
        req = Immutable.fromJS({
            context: _require('tests/mock/session').get('dimensions').toJS()
        });
        params = req.merge({
            theme: undefined
        });
    });

    describe('#read', function () {
        describe('#ok', function () {
            describe('.single', function () {
                it('{ }', function (done) {
                    service.read(req.toJS(), 'theme', params.toJS(), {}, function (err, result) {
                        expect(result).to.be.null;
                        expect(err).to.be.deep.equals(
                            fumble.http.internalServerError(JSON.stringify([
                                'Missing required parameter: site',
                                'Missing required parameter: theme'
                            ]))
                        );
                        done();
                    });
                });
            });
        });
    });
});
